import React, { useState } from "react";
import Btn from "./Btn";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  MenuItem,
  Menu,
} from "@material-ui/core";
import NavBtn from "./NavBtn";
import MenuIcon from "@material-ui/icons/Menu";
const NavBar = () => {
  const [mobileMenuAnchorEl, setMobileMenuAnchorEl] = useState(null);
  const isMobileMenuOpen = Boolean(mobileMenuAnchorEl);
  const openMobileMenu = (event) => {
    setMobileMenuAnchorEl(event.currentTarget);
  };
  const closeMobileMenu = () => {
    setMobileMenuAnchorEl(null);
  };
  const mobileMenu = (
    <Menu
      anchorEl={mobileMenuAnchorEl}
      id="mobile-menu"
      keepMounted
      open={isMobileMenuOpen}
    >
      <MenuItem onClick={closeMobileMenu}>
        <Btn link="/" btnName="Home" />
      </MenuItem>
      <MenuItem onClick={closeMobileMenu}>
        <Btn link="/about" btnName="About" />
      </MenuItem>
      <MenuItem onClick={closeMobileMenu}>
        <Btn link="/contact" btnName="Contact" />
      </MenuItem>
    </Menu>
  );
  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" style={{ flexGrow: 1 }}>
            Sunny
          </Typography>
          <div className=" d-none d-xxl-block d-xl-block d-lg-block">
            <NavBtn />
          </div>
          <div className=" d-block d-xxl-none d-xl-none d-lg-none ">
            <IconButton color="inherit" onClick={openMobileMenu}>
              <MenuIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>
      {mobileMenu}
    </>
  );
};
export default NavBar;
